mapper.insert:
  		INSERT INTO FILES (
							   FILE_ID
							   , BOARD_ID
							   , ARTICLE_ID
							   , ORIGIN_FILE_NAME
							   , FILE_NAME
							   , CONTENT
		) VALUES ( 
								'FL' || #{boardId} || '-' || TO_CHAR(SYSDATE, 'YYYYMMDDHH24') || '-' || LPAD(FILE_ID_SEQ.NEXTVAL, 6, '0')
								, #{boardId}
								, #{articleId}
								, #{originFileName}
								, #{fileName}
								, #{content}
		)

mapper.select:
  		SELECT	FILE_ID
  				, BOARD_ID
  				, ARTICLE_ID
  				, ORIGIN_FILE_NAME
  				, FILE_NAME
  				, CONTENT
		FROM	FILES
		WHERE	BOARD_ID = #{boardId}
		AND		ARTICLE_ID = #{articleId}
		AND		FILE_ID = #{fileId}

mapper.delete:
  		DELETE
  		FROM	FILES
  		WHERE	BOARD_ID = #{boardId}
		AND		ARTICLE_ID = #{articleId}
		AND		FILE_ID = #{fileId}

  		DELETE
  		FROM	FILES
  		WHERE	BOARD_ID = #{boardId}
		AND		ARTICLE_ID = #{articleId}
