mapper.insert:
  		INSERT	INTO RECOMMEND (
  									RECOMMEND_ID
  									, BOARD_ID
  									, ARTICLE_ID
  									, EMAIL
  		) VALUES (
  									'RC' || #{boardId} || '-' || TO_CHAR(SYSDATE, 'YYYYMMDDHH24') || '-' || LPAD(RECOMMEND_ID_SEQ.NEXTVAL, 6, '0')
									, #{boardId}
									, #{articleId}
									, #{email}
  		)

mapper.select:
SELECT	COUNT(1)
FROM	RECOMMEND
WHERE	BOARD_ID = #{boardId} 
AND 	ARTICLE_ID = #{articleId}
AND		EMAIL = #{email}

mapper.delete:
DELETE
FROM	RECOMMEND
WHERE	BOARD_ID = #{boardId} 
AND 	ARTICLE_ID = #{articleId}
AND		EMAIL = #{email}

DELETE
FROM	RECOMMEND
WHERE	BOARD_ID = #{boardId} 
AND 	ARTICLE_ID = #{articleId}

mapper.select:
SELECT	COUNT(1)
FROM	RECOMMEND
WHERE	BOARD_ID = #{boardId} 
AND 	ARTICLE_ID = #{articleId}
